import React from 'react';
import { Link } from 'react-router-dom';
import Slider from '../components/Slider';  // 슬라이더 컴포넌트
import LoginForm from '../components/LoginForm';  // 로그인 폼 컴포넌트

function HomePage({ setIsLoggedIn }) { // props로 setIsLoggedIn 받음
    return (
        <div className="container">
            <div className="main-content">
                {/* 좌측 슬라이더 */}
                <div className="left-content">
                    <Slider />
                </div>

                {/* 우측 로그인 폼 */}
                <div className="right-content">
                    <h2>마음을 잇는 당신의 곁에, 마음읽기</h2>
                    <LoginForm setIsLoggedIn={setIsLoggedIn} /> {/* 전달 */}
                </div>
            </div>
        </div>
    );
}

export default HomePage;
